mapper.select:
SELECT * FROM application_feature t




  ORDER BY

    ${order}



  LIMIT 0, ${length}

SELECT * FROM application_feature t
WHERE t.id in (
  SELECT entity_id  FROM application_feature_scope
  WHERE authority_id = #{authority_id}
    AND authorization_object_type = #{authorization_object_type}
    AND authorization_object_id = #{authorization_object_id}
  ) AND t.`status` = 1

SELECT * FROM application_feature
WHERE application_id = #{application_id}
      AND parent_id = '00000000-0000-0000-0000-000000000000'
      AND type = #{type}
      AND status = 1
ORDER BY order_id, code

SELECT name FROM application_feature T

  status = 1

    AND application_id = #{application_id}


    AND ${authorization_scope_sql}


ORDER BY name

mapper.delete:
delete from application_feature_scope where entity_id=#{entityId}
and entity_class_name=#{entityClassName} and authority_id=#{authorityId}
and authorization_object_type=#{authorizationObjectType}
and authorization_object_id=#{authorizationObjectId}
