mapper.update:
UPDATE application SET
parent_id = #{parentId}, code = #{code}, application_name = #{applicationName}, application_display_name = #{applicationDisplayName}, application_key = #{applicationKey}, application_secret = #{applicationSecret}, pinyin = #{pinyin}, description = #{description}, administrator_email = #{administratorEmail}, icon_path = #{iconPath}, big_icon_path = #{bigIconPath}, help_url = #{helpUrl}, license_status = #{licenseStatus}, hidden = #{hidden}, locking = #{locking}, order_id = #{orderId}, status = #{status}, remark = #{remark}, modified_date = CURRENT_TIMESTAMP
WHERE Id = #{Id} AND modified_date = #{ModifiedDate}

mapper.select:
SELECT * FROM application_menu T


    AND menu_type = #{param_menu_type}


    AND application_id = #{param_application_id}


    AND parent_id = #{param_parent_id}


    AND status = #{param_status}



  ORDER BY

    #{order}



  LIMIT 0, ${length}
