mapper.update:
UPDATE application_menu SET
application_id = #{applicationId,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, target = #{target,jdbcType=VARCHAR}, menu_type = #{menuType,jdbcType=VARCHAR}, icon_path = #{iconPath,jdbcType=VARCHAR}, big_icon_path = #{bigIconPath,jdbcType=VARCHAR}, display_type = #{displayType,jdbcType=VARCHAR}, has_child = #{hasChild,jdbcType=INTEGER}, context_object = #{contextObject,jdbcType=VARCHAR}, order_id = #{orderId,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR}, full_path = #{fullPath,jdbcType=VARCHAR}, modified_date = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT * FROM application_menu T


    AND menu_type = #{param_menu_type}


    AND application_id = #{param_application_id}


    AND parent_id = #{param_parent_id}


    AND status = #{param_status}



  ORDER BY

    #{order}



  LIMIT 0, ${length}
