mapper.update:
UPDATE application_setting SET
application_id = #{applicationId,jdbcType=VARCHAR}, application_setting_group_id = #{applicationSettingGroupId,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, text = #{text,jdbcType=VARCHAR}, value = #{value,jdbcType=VARCHAR}, order_id = #{orderId,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR}, modified_date = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT * FROM application_setting




  ORDER BY

    ${order}



  LIMIT 0, ${length}

SELECT * FROM application_setting
WHERE application_setting_group_id = #{applicationSettingGroupId}

  AND text LIKE #{keyword}

ORDER BY order_id, text

SELECT * FROM application_setting WHERE application_setting_group_id = ( SELECT id FROM application_setting_group WHERE name = #{applicationSettingGroupName} )
AND text LIKE #{keyword}
ORDER BY order_id, text
