import org.junit.Test;

import java.time.LocalDateTime;

import static org.junit.Assert.*;

public class ByteUtilTests {
  @Test
  public void testGetDefaultTime() {
    LocalDateTime date = DateUtil.getDefaultLocalDateTime();
    String dateText =  date.toString();
    assertTrue("DateUtil.getDefaultTime() should return " + dateText, "1970-01-01T00:00".equals(dateText));
  }

  @Test
  public void testGetTimestamp() {
    LocalDateTime now = LocalDateTime.now();

    long timestamp = DateUtil.getTimestamp();

    long timestamp2 = DateUtil.getTimestamp(now);

    assertEquals("DateUtil.getDefaultTime() should return 1970-01-01T00:00", timestamp, timestamp2);
  }

  @Test
  public void testToLocalDateTime() {
    LocalDateTime datetime = DateUtil.toLocalDateTime(1527407368L);

    // assertEquals("DateUtil.getDefaultTime() should return 1970-01-01T00:00", "1970-01-01T00:00", datetime);
  }
}
